pages/home:
---
products:
    new:
        limit: {{theme_settings.homepage_new_products_count}}
    featured:
        limit: {{theme_settings.homepage_featured_products_count}}
    top_sellers:
        limit: {{theme_settings.homepage_top_products_count}}
carousel: {{theme_settings.homepage_show_carousel}}
blog:
    recent_posts:
        limit: {{theme_settings.homepage_blog_posts_count}}
---
{{#partial "hero"}}
    {{{region name="home_below_menu"}}}
    {{#and carousel carousel.slides.length}}
        {{> components/carousel arrows=theme_settings.homepage_show_carousel_arrows play_pause_button=theme_settings.homepage_show_carousel_play_pause_button}}
    {{/and}}
    {{{region name="home_below_carousel"}}}
        {{#if products.top_sellers}}
        <div class="top-seller-carousel">
            {{> components/products/top products=products.top_sellers columns=theme_settings.homepage_top_products_column_count}}
        </div>
    {{/if}}
    {{{region name="home_below_top_products"}}}
{{/partial}}

{{#partial "page"}}

    {{#each shipping_messages}}
        {{> components/common/alert/alert-info message}}
    {{/each}}

<div class="main full">
    {{#if products.featured}}
        {{> components/products/featured products=products.featured columns=theme_settings.homepage_featured_products_column_count}}
    {{/if}}
    {{{region name="home_below_featured_products"}}}
    {{#if products.new}}
        {{> components/products/new products=products.new columns=theme_settings.homepage_new_products_column_count}}
    {{/if}}
    {{{region name="home_below_new_products"}}}
</div>
{{/partial}}
{{> layout/base}}

components/common/footer:
{{#if banners.bottom}}
    <div class="banners" data-banner-location="bottom">
        {{#each (limit banners.bottom_metadata 1)}}
            <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                <div data-event-type="promotion-click">
                    {{{this.content}}}
                </div>
            </div>
        {{/each}}
    </div>
{{/if}}
{{{region name="footer-top"}}} 
{{{region name="footer-top--global"}}} 
<footer class="footer" role="contentinfo">
    <h2 class="footer-title-sr-only">{{lang 'footer.title'}}</h2>
    <div class="footer-top">
        <div class="container">
            <section class="footer-info">
                <article class="footer-info-col footer-info-col--small" data-section-type="footer-links-1">
                    {{{region name="footer-links-1--global"}}}                    
                    {{#if theme_settings.social_icon_placement_bottom '==' 'bottom_left'}}
                        {{> components/common/social-links}}
                    {{/if}}
                </article>

                <article class="footer-info-col footer-info-col--small" data-section-type="footer-links-2">                    
                    {{{region name="footer-links-2--global"}}}
                </article>

                <article class="footer-info-col footer-info-col--small" data-section-type="footer-links-3">                    
                    {{{region name="footer-links-3--global"}}}
                </article>

                <article class="footer-info-col footer-info-col--small" data-section-type="footer-links-4">                    
                    {{{region name="footer-links-4--global"}}}
                </article>

                {{#and theme_settings.shop_by_brand_show_footer shop_by_brand.length}}
                    <article class="footer-info-col footer-info-col--small" data-section-type="footer-brands">
                        <h3 class="footer-info-heading">{{lang 'footer.brands'}}</h3>
                        <ul class="footer-info-list">
                            {{#each shop_by_brand}}
                                <li>
                                    <a href="{{url}}">{{name}}</a>
                                </li>
                            {{/each}}
                            <li><a href="{{urls.brands}}">{{lang 'common.view_all'}}</a></li>
                        </ul>
                    </article>
                {{/and}}

                <article class="footer-info-col" data-section-type="newsletterSubscription">                
                    {{#if settings.address}}
                        <h3 class="footer-info-heading">{{lang 'footer.info'}}</h3>
                        <address>{{nl2br settings.address}}</address>
                        {{#if settings.phone_number}}
                            <strong>{{lang 'footer.call_us' phone_number=settings.phone_number}}</strong>
                        {{/if}}
                    {{/if}}
                    {{#if settings.show_newsletter_box}}
                        {{> components/common/subscription-form}}
                    {{/if}}
                    {{#if theme_settings.social_icon_placement_bottom '==' 'bottom_right'}}
                        {{> components/common/social-links}}
                    {{/if}}
                    {{> components/common/payment-icons}}
                </article>
            </section>
        </div>
    </div>
    
    <div class="footer-bottom">
        <div class="container">
            {{#if theme_settings.show_powered_by}}
                <div class="footer-copyright">
                    <p class="powered-by">{{lang 'footer.powered_by'}} <a href="https://www.bigcommerce.com?utm_source=merchant&amp;utm_medium=poweredbyBC" rel="nofollow">BigCommerce</a></p>
                </div>
            {{/if}}
            {{#if theme_settings.show_copyright_footer}}
                <div class="footer-copyright">
                    <p class="powered-by">Copyright {{moment format="YYYY"}} &copy; {{settings.store_name}} </p>
                </div>
            {{/if}}
            <div class="footer-bottom-links">
                {{{region name="footer-bottom--global"}}}
            </div>
        </div>
    </div>
    {{{region name="ssl_site_seal--global"}}}
</footer>

components/products/product-badge:
{{#if badge_view '===' 'sash'}}
    <div class="{{badge-type}}-flag-sash">
        <span class="{{badge-type}}-text">{{{badge_label}}}</span>
    </div>
{{else if badge_view '===' 'topleft'}}
    <div class="{{badge-type}}-flag-side badge-at-top-left">
        <span class="{{badge-type}}-text">
            {{#if badge-type '===' 'sale'}}
                <svg class="icon sale-icon">
                    <use href="#icon-sale" />
                </svg>
            {{/if}}
            {{{badge_label}}}
        </span>
    </div>
{{else if badge_view '===' 'burst'}}
    <div class="starwrap">
        <div class="{{badge-type}}-text-burst">{{{badge_label}}}</div>
        <div class="{{badge-type}}-flag-star"></div>
    </div>
{{/if}}

components/products/price:
{{!-- There are 2 code paths for generating the PDP pricing HTML determined by the following #and condition.
If a 'price_range' exists then we generate all HTML in price-range.html, otherwise it is defined here. Both code
paths generate the same HTML structure with some differences in whether that element is displayed by default (css styling).

If you are making a change here or in price-range.html, you probably want to make that change in both files. --}}

{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
    {{> components/products/price-range price=price}}
{{else}}
    <div class="price-section price-section--withTax rrp-price--withTax" {{#unless price.rrp_with_tax}}style="display: none;"{{/unless}}>
        <span>
            {{> components/products/price-label
                label_value=theme_settings.pdp-retail-price-label
                fallback=(lang "page_builder.pdp-retail-price-label")
            }}
        </span>
        <span data-product-rrp-with-tax class="price price--rrp">
            {{price.rrp_with_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withTax non-sale-price--withTax" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
        <span>
            {{> components/products/price-label
                label_value=theme_settings.pdp-non-sale-price-label
                fallback=(lang "page_builder.pdp-non-sale-price-label")
            }}
        </span>
        <span data-product-non-sale-price-with-tax class="price price--non-sale">
            {{price.non_sale_price_with_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withTax price--withTax" {{#unless price.with_tax}}style="display: none;{{/unless}}>
        <span class="price-label" {{#if price.non_sale_price_with_tax}}style="display: none;"{{/if}}>
            {{theme_settings.pdp-price-label}}
        </span>
        <span class="price-now-label" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
            {{> components/products/price-label
                label_value=theme_settings.pdp-sale-price-label
                fallback=(lang "page_builder.pdp-sale-price-label")
            }}
        </span>
        <span data-product-price-with-tax class="price">{{price.with_tax.formatted}}</span>
        {{#if price.without_tax}}
            <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
        {{/if}}
    </div>
    <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" {{#unless price.rrp_without_tax}}style="display: none;"{{/unless}}>
        <span>
            {{> components/products/price-label
                label_value=theme_settings.pdp-retail-price-label
                fallback=(lang "page_builder.pdp-retail-price-label")
            }}
        </span>
        <span data-product-rrp-price-without-tax class="price price--rrp">
            {{price.rrp_without_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withoutTax non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
        <span>
            {{> components/products/price-label
                label_value=theme_settings.pdp-non-sale-price-label
                fallback=(lang "page_builder.pdp-non-sale-price-label")
            }}
        </span>
        <span data-product-non-sale-price-without-tax class="price price--non-sale">
            {{price.non_sale_price_without_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withoutTax price--withoutTax" {{#unless price.without_tax}}style="display: none;"{{/unless}}>
        <span class="price-label" {{#if price.non_sale_price_without_tax}}style="display: none;"{{/if}}>
            {{theme_settings.pdp-price-label}}
        </span>
        <span class="price-now-label" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
            {{> components/products/price-label
                label_value=theme_settings.pdp-sale-price-label
                fallback=(lang "page_builder.pdp-sale-price-label")
            }}
        </span>
        <span data-product-price-without-tax class="price{{#if price.with_tax}} price-section--minor{{/if}}">{{price.without_tax.formatted}}</span>
        {{#if price.with_tax}}
            <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
        {{/if}}
    </div>
    {{#if page_type '===' 'product'}}
         <div class="price-section price-section--saving price" {{#unless price.saved}}style="display: none;"{{/unless}}>
                <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                <span data-product-price-saved class="price price--saving">
                    {{price.saved.formatted}}
                </span>
                <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
         </div>
    {{/if}}
{{/and}}

components/products/bulk-discount-rates:
{{#if bulk_discount_rates.length}}
